package Nunmber;

public class NumberText {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
byte b=100;
short s=30000;
int i=4000000;
long l=200000000;
long lo=b+s+i+l;
float f=12.322f;
double d=23.00001d;
double dou=3000.0000001;
double fl=f+d+dou;
System.out.println("结果是："+lo);
System.out.printf("第二结果是："+fl);
	}
}
